package com.tahsinrahit.ludu;

public class PositionMap {
	private int id;
	private int x;
	private int y;
	
	public PositionMap() {
		// TODO Auto-generated constructor stub
	}

	public PositionMap(int id, int x, int y) {
		super();
		this.id = id;
		this.x = x;
		this.y = y;
	}

	public int getId() {
		return id;
	}

	public int getX() {
		return x;
	}

	public int getY() {
		return y;
	}
	
	
	

}
